import { Component , OnInit} from '@angular/core';
const navbar = [
  { name: 'HOME', id: 'home' },
  { name: 'ABOUT', id: 'about' },
  {
    name: 'OUR PRODUCTS',
    id: 'product',
    child: [
      { name: 'Product 1', id: 'p1' },
      { name: 'Product 2', id: 'p2' },
      { name: 'Product 3', id: 'p3' },
      { name: 'Product 4', id: 'p4' },
    ],
  },
  { name: 'CONTACT US', id: 'contact' },
];

@Component({
  selector: 'app-center',
  templateUrl: './center.component.html',
  styleUrls: ['./center.component.css']
})
export class CenterComponent implements OnInit {
  navbarItems = navbar;

  constructor() {}

  ngOnInit(): void {}
}
<div class="rectangle">
    <div class="square">
        <div class="man">
          <img src="assets/Images/jessica-radanavong-IchPBHFD0pw-unsplash (1).png">
          <div class="jacket">
          <img src="assets/Images/Oregon jacket.svg">
          <h4>$124</h4>
          </div>
        </div>
      </div>
      <ul class="navbar-menu">
        <li *ngFor="let item of navbarItems">
          <a [routerLink]="[item.id]" [ngClass]="item.id">{{ item.name }}</a>
          <ul *ngIf="item.child">
            <li *ngFor="let childItem of item.child">
              <a [routerLink]="[item.id, childItem.id]">{{ childItem.name }}
              </a>
            </li>
          </ul>
        </li>
      </ul>

</div>
